import React from 'react';

import TodoListCss from './TodoListCss.module.css';

// create new class component for TODO List
class TodoList extends React.Component {
  constructor(props) {
    super(props);
    // create state only in constructor
    this.state = {
      newItem: '',
      list: [],
    };
    this.onKeyPressHandler = this.onKeypressHandler.bind(this);
  }

  updateInput(key, value) {
    //update react state
    this.setState({
      [key]: value,
    });
  }

  addItem() {
    //create item with unique id
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem.slice(),
    };

    // copy of current list of items
    const list = [...this.state.list];

    //add new item to list
    list.push(newItem);

    // update state with new list and reset newItem input
    this.setState({
      list,
      newItem: '',
    });
  }

  onKeyPressHandler(e) {
    if (e.keyCode === 13) {
      return this.addItem();
    }
  }

  deleteItem(id) {
    //copy current list of items
    const list = [...this.state.list];

    //filter out item being deleted
    const updatedList = list.filter((item) => item.id !== id);

    this.setState({ list: updatedList });
  }

  render() {
    return (
      <div className={TodoListCss.TodoList}>
        <div>
          Add an Item...
          <br />
          <input
            type="text"
            placeholder="Type your TODO here..."
            value={this.state.newItem}
            onChange={(e) => this.updateInput('newItem', e.target.value)}
            onKeyPress={this.onKeyPressHandler}
          />
          <button onClick={() => this.addItem()}>App TODO</button>
          <br />
          <ul>
            {this.state.list.map((item) => {
              return (
                //add li with unique id
                <li key={item.id}>
                  {item.value}

                  <button onClick={() => this.deleteItem(item.id)}>X</button>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default TodoList;
